noFill_draw_one.sourceCode:
function setup() {
  createCanvas(120, 120);
  background(255);
  noFill();
  ellipse(width/2, height/2, 10, 10);
}

noFill_loop_draw.sourceCode:
function setup() {
  createCanvas(120, 120);
  background(255);
  noFill();
  for(let i = 0; i < 10; i++){
    let d = (i + 1) * 10;
    ellipse(width/2, height/2, d, d);
  }
}

