  let count = 0;
let cycle = 100;
let increment = 1;
function setup(){
  createCanvas(200, 200);
}

draw_heartbeat_var_change.drawFunction:
function draw(){
  background(160, 192, 255);
 count = (count + increment) % cycle;
  if (count < cycle/2) {
 size = count + 50;
 } else {
 size = (cycle - count) + 50;
 }
 ellipse(width/2, height/2, size);
}

